import React, { useContext, useEffect, useState, useRef } from "react";

import classes from "./HeaderCartButton.module.css";

import CartIcon from "../Cart/CartIcon";

import CartContext from "../../store/cart-context";

function HeaderCartButton(props) {
  const [buttonIsHighlighted, setButtonIsHighlighted] = useState(false);
  const cartCtx = useContext(CartContext);

  const cartButton = useRef();

  const numerOfCartItems = cartCtx.items.reduce(
    (currNumber, item) => currNumber + item.amount,
    0
  );

  const buttonClasses = `${classes.button} ${buttonIsHighlighted ? classes.bump : ""}`;

  useEffect(() => {
    if (cartCtx.items.length === 0) {
      return;
    }
    setButtonIsHighlighted(true);

    setTimeout(() => {
      cartButton.current.classList.remove(classes.bump);
      setButtonIsHighlighted(false);
    }, 100)
  }, [cartCtx.items])

  return (
    <button ref={cartButton} className={buttonClasses} onClick={props.openCart}>
      <span className={classes.icon}>
        <CartIcon />
      </span>
      <span>Your Cart</span>
      <span className={classes.badge}>{numerOfCartItems}</span>
    </button>
  );
}

export default HeaderCartButton;
